	function  updateCartAjax(tsk,product_price_code){
	var qty  = $("#product_qty"+product_price_code).val().trim();
	
 	if(tsk=='plus'){
 		
		var product_qty = parseInt(qty) + parseInt(1);
		
	}
	else{
		var product_qty = parseInt(qty) - parseInt(1);
	}
	if(product_qty<=0){
		product_qty = 1;
	}
	
	$("#product_qty"+product_price_code).val(product_qty);
	
	}